  const [queryString, setQueryString] = useState(url);
  const [ipInfo, setIpInfo] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(
    async () => {
      setLoading(true);
      const response = await fetch(queryString);
      let result = "";
